import Dandy from 0xDandy
import NonFungibleToken from 0xNonFungibleToken
import MetadataViews from 0xMetadataViews
import FlowToken from 0xFlowToken
import FungibleToken from 0xFungibleToken
import NFTStorefrontV2 from 0xNFTStorefrontV2
// This transaction was auto-generated with the NFT Catalog (https://github.com/dapperlabs/nft-catalog)
//
// This transaction facilitates the purchase of a listed NFT with the StorefrontV2 contract 
// 
// Collection Identifier: Dandy
// Vault Identifier: flow
//
// Version: 0.1.1

transaction(listingResourceID: UInt64, storefrontAddress: Address, commissionRecipient: Address?) {
    /// `listingResourceID` - ID of the Storefront listing resource
    /// `storefrontAddress` - The address that owns the storefront listing
    /// `commissionRecipient` - Optional recipient for transaction commission if comission exists.
    let paymentVault: @FungibleToken.Vault
    let nftCollection: &Dandy.Collection{NonFungibleToken.CollectionPublic,NonFungibleToken.Receiver,MetadataViews.ResolverCollection,Dandy.CollectionPublic}
    let storefront: &NFTStorefrontV2.Storefront{NFTStorefrontV2.StorefrontPublic}
    let listing: &NFTStorefrontV2.Listing{NFTStorefrontV2.ListingPublic}
    var commissionRecipientCap: Capability<&{FungibleToken.Receiver}>?

    prepare(acct: AuthAccount) {
        self.commissionRecipientCap = nil

        // Set up NFT to make sure this account has NFT setup correctly
        if acct.borrow<&Dandy.Collection>(from: /storage/findDandy) == nil {
            let collection <- Dandy.createEmptyCollection()
            acct.save(<-collection, to: /storage/findDandy)
            }
        if (acct.getCapability<&Dandy.Collection{NonFungibleToken.CollectionPublic,NonFungibleToken.Receiver,MetadataViews.ResolverCollection,Dandy.CollectionPublic}>(/public/findDandy).borrow() == nil) {
            acct.unlink(/public/findDandy)
            acct.link<&Dandy.Collection{NonFungibleToken.CollectionPublic,NonFungibleToken.Receiver,MetadataViews.ResolverCollection,Dandy.CollectionPublic}>(/public/findDandy, target: /storage/findDandy)
        }

        if (acct.getCapability<&Dandy.Collection{NonFungibleToken.Provider,NonFungibleToken.CollectionPublic,NonFungibleToken.Receiver,MetadataViews.ResolverCollection,Dandy.CollectionPublic}>(/private/findDandy).borrow() == nil) {
            acct.unlink(/private/findDandy)
            acct.link<&Dandy.Collection{NonFungibleToken.Provider,NonFungibleToken.CollectionPublic,NonFungibleToken.Receiver,MetadataViews.ResolverCollection,Dandy.CollectionPublic}>(/private/findDandy, target: /storage/findDandy)
        }

        // Access the storefront public resource of the seller to purchase the listing.
        self.storefront = getAccount(storefrontAddress)
            .getCapability<&NFTStorefrontV2.Storefront{NFTStorefrontV2.StorefrontPublic}>(
                NFTStorefrontV2.StorefrontPublicPath
            )!
            .borrow()
            ?? panic("Could not borrow Storefront from provided address")

        // Borrow the listing
        self.listing = self.storefront.borrowListing(listingResourceID: listingResourceID)
                    ?? panic("No Offer with that ID in Storefront")
        let price = self.listing.getDetails().salePrice

        // Access the vault of the buyer to pay the sale price of the listing.
        let mainFTVault = acct.borrow<&FlowToken.Vault>(from: /storage/flowTokenVault)
            ?? panic("Cannot borrow Fungible Token vault from acct storage")
        self.paymentVault <- mainFTVault.withdraw(amount: price)

        // Access the buyer's NFT collection to store the purchased NFT.
        self.nftCollection = acct.borrow<&Dandy.Collection{NonFungibleToken.CollectionPublic,NonFungibleToken.Receiver,MetadataViews.ResolverCollection,Dandy.CollectionPublic}>(
            from: /storage/findDandy
        ) ?? panic("Cannot borrow NFT collection receiver from account")

        // Fetch the commission amt.
        let commissionAmount = self.listing.getDetails().commissionAmount

        if commissionRecipient != nil && commissionAmount != 0.0 {
            // Access the capability to receive the commission.
            let _commissionRecipientCap = getAccount(commissionRecipient!).getCapability<&{FungibleToken.Receiver}>(/public/flow)
            assert(_commissionRecipientCap.check(), message: "Commission Recipient doesn't have flowtoken receiving capability")
            self.commissionRecipientCap = _commissionRecipientCap
        } else if commissionAmount == 0.0 {
            self.commissionRecipientCap = nil
        } else {
            panic("Commission recipient can not be empty when commission amount is non zero")
        }
    }

    execute {
        // Purchase the NFT
        let item <- self.listing.purchase(
            payment: <-self.paymentVault,
            commissionRecipient: self.commissionRecipientCap
        )
        // Deposit the NFT in the buyer's collection.
        self.nftCollection.deposit(token: <-item)
    }
}
